import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { withRouter } from 'react-router-dom';
import AppNavbar from './AppNavbar';

class ClientEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: null,
            name: '',
            email: '',
            modal: false
        };
        this.toggle = this.toggle.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        const { match } = this.props;
        if (match.params.id !== 'new') {
            fetch(`/api/usuario/${match.params.id}`)
                .then(response => response.json())
                .then(data => {
                    this.setState({
                        id: data.id,
                        name: data.name,
                        email: data.email
                    });
                })
                .catch(error => console.error('Error fetching client:', error));
        }
    }

    toggle() {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    async handleSubmit(event) {
        event.preventDefault();
        const { id, name, email } = this.state;

        try {
            const response = await fetch(`/api/usuario${id ? `/${id}` : ''}`, {
                method: id ? 'PUT' : 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ name, email })
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            this.props.history.push('/clients');
        } catch (error) {
            console.error('Error saving client:', error);
        }
    }

    render() {
        const { name, email, modal } = this.state;

        return (
            <div>
                <AppNavbar />
                <Modal isOpen={modal} toggle={this.toggle}>
                    <ModalHeader toggle={this.toggle}>Edit Client</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleSubmit}>
                            <FormGroup>
                                <Label for="name">Name</Label>
                                <Input type="text" name="name" id="name" value={name} onChange={this.handleChange} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="email">Email</Label>
                                <Input type="text" name="email" id="email" value={email} onChange={this.handleChange} />
                            </FormGroup>
                            <Button color="primary" type="submit">Save</Button>{' '}
                            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default withRouter(ClientEdit);
