import React, { Component } from 'react';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import AppNavbar from './AppNavbar';

class ClientEdit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: null,
            name: '',
            email: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async componentDidMount() {
        const { id } = this.props.match.params;
        if (id !== 'new') {
            const response = await fetch(`/api/usuario/${id}`);
            const data = await response.json();
            this.setState({
                id: data.id,
                name: data.name,
                email: data.email
            });
        }
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    async handleSubmit(event) {
        event.preventDefault();
        const { id, name, email } = this.state;
        const url = id ? `/api/usuario/${id}` : '/api/usuario';
        const method = id ? 'PUT' : 'POST';
        const body = JSON.stringify({ name, email });

        await fetch(url, {
            method,
            headers: {
                'Content-Type': 'application/json'
            },
            body
        });

        this.props.history.push('/clients');
    }

    render() {
        const { name, email } = this.state;

        return (
            <div>
                <AppNavbar />
                <Container>
                    <h2>{this.state.id ? 'Edit Client' : 'Add Client'}</h2>
                    <Form onSubmit={this.handleSubmit}>
                        <FormGroup>
                            <Label for="name">Name</Label>
                            <Input type="text" name="name" id="name" value={name} onChange={this.handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="text" name="email" id="email" value={email} onChange={this.handleChange} />
                        </FormGroup>
                        <Button color="primary" type="submit">Save</Button>{' '}
                        <Button color="secondary" onClick={() => this.props.history.push('/clients')}>Cancel</Button>
                    </Form>
                </Container>
            </div>
        );
    }
}

export default ClientEdit;
