import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Modal, ModalHeader, ModalBody, ModalFooter, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';

class ClientList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clients: [],
            isLoading: true,
            modal: false,
            id: null,
            name: '',
            email: ''
        };
        this.toggleModal = this.toggleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.remove = this.remove.bind(this);
    }

    componentDidMount() {
        this.fetchClients();
    }

    async fetchClients() {
        try {
            const response = await fetch('/api/usuario');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            this.setState({ clients: data, isLoading: false });
        } catch (error) {
            console.error('Error fetching clients:', error);
            this.setState({ isLoading: false });
        }
    }

    toggleModal(id) {
        if (id) {
            // Edit mode
            const client = this.state.clients.find(c => c.id === id);
            this.setState({
                modal: true,
                id: client.id,
                name: client.name,
                email: client.email
            });
        } else {
            // New client mode
            this.setState({
                modal: true,
                id: null,
                name: '',
                email: ''
            });
        }
    }

    async handleSubmit(event) {
        event.preventDefault();
        const { id, name, email } = this.state;

        try {
            const response = await fetch(`/api/usuario${id ? `/${id}` : ''}`, {
                method: id ? 'PUT' : 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ name, email })
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Update state and close modal
            const updatedClients = id
                ? this.state.clients.map(c => (c.id === id ? { ...c, name, email } : c))
                : [...this.state.clients, { id: Date.now(), name, email }];

            this.setState({
                clients: updatedClients,
                modal: false,
                id: null,
                name: '',
                email: ''
            });
        } catch (error) {
            console.error('Error saving client:', error);
        }
    }

    async remove(id) {
        try {
            await fetch(`/api/usuario/${id}`, {
                method: 'DELETE'
            });
            let updatedClients = this.state.clients.filter(client => client.id !== id);
            this.setState({ clients: updatedClients });
        } catch (error) {
            console.error('Error deleting client:', error);
        }
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    }

    render() {
        const { clients, isLoading, modal, id, name, email } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        const clientList = clients.map(client => (
            <tr key={client.id}>
                <td style={{ whiteSpace: 'nowrap' }}>{client.name}</td>
                <td>{client.email}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" onClick={() => this.toggleModal(client.id)}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(client.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        ));

        return (
            <div>
                <AppNavbar />
                <Container fluid>
                    <div className="float-right">
                        <Button color="success" onClick={() => this.toggleModal()}>Add Client</Button>
                    </div>
                    <h3>Clients</h3>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="30%">Name</th>
                            <th width="30%">Email</th>
                            <th width="40%">Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {clientList}
                        </tbody>
                    </Table>
                </Container>

                <Modal isOpen={modal} toggle={() => this.toggleModal()}>
                    <ModalHeader toggle={() => this.toggleModal()}>{id ? 'Edit Client' : 'Add Client'}</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleSubmit}>
                            <FormGroup>
                                <Label for="name">Name</Label>
                                <Input type="text" name="name" id="name" value={name} onChange={this.handleChange} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="email">Email</Label>
                                <Input type="text" name="email" id="email" value={email} onChange={this.handleChange} />
                            </FormGroup>
                            <Button color="primary" type="submit">Save</Button>{' '}
                            <Button color="secondary" onClick={() => this.toggleModal()}>Cancel</Button>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default ClientList;
